    fixed = TRUE)

  # Testing softmax(data = df, cols = c("l1", "l2", "l3"),...
  # Assigning output
  output_16124 <- softmax(data = df, cols = c("l1", "l2", "l3"), axis = "c")
  # Testing class
  expect_equal(
    class(output_16124),
    c("tbl_df", "tbl", "data.frame"),
