    xpectr::element_types(output_19032),
    c("double", "double", "double", "integer"),
    fixed = TRUE)
  # Testing dimensions
  expect_equal(
    dim(output_19032),
    c(7L, 4L))
  # Testing group keys
  expect_equal(
    colnames(dplyr::group_keys(output_19032)),
    character(0),
    fixed = TRUE)

  ## Finished testing 'softmax'                                               ####


})
